package org.cardanofoundation.hydra.client;

import com.fasterxml.jackson.databind.ObjectMapper;
import com.github.tomakehurst.wiremock.WireMockServer;
import com.github.tomakehurst.wiremock.client.WireMock;
import org.cardanofoundation.hydra.core.model.UTXO;
import org.cardanofoundation.hydra.core.model.http.HydraProtocolParameters;
import org.junit.jupiter.api.AfterAll;
import org.junit.jupiter.api.BeforeAll;
import org.junit.jupiter.api.Test;

import java.math.BigInteger;
import java.net.http.HttpClient;
import java.time.Duration;
import java.util.Collections;
import java.util.Map;

import static com.github.tomakehurst.wiremock.client.WireMock.*;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;

class HydraWebClientTest {

    private static WireMockServer wireMockServer;

    private final static ObjectMapper objectMapper = new ObjectMapper();

    private final static String PROTOCOL_PARAMETERS_RESPONSE = "{\"minFeeA\":0,\"minFeeB\":0,\"maxBlockBodySize\":65536,\"maxTxSize\":16384,\"maxBlockHeaderSize\":1100,\"keyDeposit\":400000,\"poolDeposit\":500000000,\"eMax\":18,\"nOpt\":50,\"a0\":0.1,\"rho\":1.78650067e-3,\"tau\":0.1,\"protocolVersion\":{\"major\":7,\"minor\":0},\"minPoolCost\":0,\"costmdls\":{},\"prices\":{\"prMem\":0,\"prSteps\":0},\"maxTxExUnits\":{\"exUnitsMem\":9223372036854775806,\"exUnitsSteps\":9223372036854775806},\"maxBlockExUnits\":{\"exUnitsMem\":80000000,\"exUnitsSteps\":40000000000},\"maxValSize\":5000,\"collateralPercentage\":150,\"maxCollateralInputs\":3,\"coinsPerUTxOByte\":0}‚èé\n";

    private final static String HEAD_COMMIT_RESPONSE = "{\"cborHex\":\"\",\"description\":\"Hydra commit transaction\",\"type\":\"Tx BabbageEra\"}\n";

    @BeforeAll
    static void beforeAll() {
        wireMockServer = new WireMockServer(0);
        wireMockServer.start();
        WireMock.configureFor(wireMockServer.port());
    }

    @AfterAll
    static void afterAll() {
        wireMockServer.stop();
    }

    @Test
    public void testProtocolParametersFetch() {
        // Mocking WireMock to respond with a static JSON
        stubFor(get(urlPathMatching("/protocol-parameters"))
                .willReturn(aResponse()
                        .withHeader("Content-Type", "application/json")
                        .withBody(PROTOCOL_PARAMETERS_RESPONSE)));

        var baseUrl = "http://localhost:" + wireMockServer.port(); // WireMock server URL
        var client = HttpClient.newHttpClient();

        var hydraWebClient = new HydraWebClient(client, objectMapper, baseUrl);

        HydraProtocolParameters hydraProtocolParameters = hydraWebClient.fetchProtocolParameters(Duration.ofMinutes(1));

        assertNotNull(hydraProtocolParameters);

        assertEquals(0, hydraProtocolParameters.getMinFeeA());
        assertEquals(0, hydraProtocolParameters.getMinFeeB());

        assertEquals(65536, hydraProtocolParameters.getMaxBlockBodySize());
        assertEquals(16384, hydraProtocolParameters.getMaxTxSize());
        assertEquals(1100, hydraProtocolParameters.getMaxBlockHeaderSize());
        assertEquals(400000, hydraProtocolParameters.getKeyDeposit());
        assertEquals(500000000, hydraProtocolParameters.getPoolDeposit());
    }

    @Test
    public void testCommitRequest() {
        // Mocking WireMock to respond with a static JSON
        stubFor(post(urlMatching("/commit"))
                .willReturn(aResponse()
                        .withHeader("Content-Type", "application/json")
                        .withBody(HEAD_COMMIT_RESPONSE)));

        var baseUrl = "http://localhost:" + wireMockServer.port();
        var client = HttpClient.newHttpClient();

        var hydraWebClient = new HydraWebClient(client, objectMapper, baseUrl);

        var utxo = UTXO.builder()
                .address("addr_test1vp0yug22dtwaxdcjdvaxr74dthlpunc57cm639578gz7algset3fh")
                .value(Map.of("lovelace", BigInteger.valueOf(50000000)))
                .build();

        var commitDataMap = Collections.singletonMap("88167818c3377fd1405880028693078a4cbf689d86834c18a71a4388cc8a216f#0", utxo);

        var headCommitResponse = hydraWebClient.commitRequest(commitDataMap, Duration.ofMinutes(1));

        assertEquals("", headCommitResponse.getCborHex());
        assertEquals("Tx BabbageEra", headCommitResponse.getType());
    }

}